mapper.select:
        select merc.id as id, user.userName as userName, user.nickname as nickname, user.password as password, 
		 user.createdAt as createdAt,   user.createdBy as createdBy,    description, merc.status as status,
		merc.phone as phone, merc.tel as tel, merc.mail as mail, 
		
		from sys_merchant merc, sys_user user
		where merc.baseUserId = user.id

      
        and baseUserId = #{baseUserId,jdbcType=INTEGER}
      
      
        and serialNo = #{serialNo,jdbcType=VARCHAR}
      
      
        and agentSerialNo = #{agentSerialNo,jdbcType=VARCHAR}
      
      
        and md5Key = #{md5Key,jdbcType=VARCHAR}
      
      
        and companyName = #{companyName,jdbcType=VARCHAR}
      
      
        and industryTypeId = #{industryTypeId,jdbcType=INTEGER}
      
      
        and industryType = #{industryType,jdbcType=VARCHAR}
      
      
        and contactMan = #{contactMan,jdbcType=VARCHAR}
      
      
        and phone = #{phone,jdbcType=VARCHAR}
      
      
        and tel = #{tel,jdbcType=VARCHAR}
      
      
        and postcode = #{postcode,jdbcType=VARCHAR}
      
      
        and prov = #{prov,jdbcType=VARCHAR}
      
      
        and city = #{city,jdbcType=VARCHAR}
      
      
        and area = #{area,jdbcType=VARCHAR}
      
      
        and address = #{address,jdbcType=VARCHAR}
      
      
        and auditStatus = #{auditStatus,jdbcType=TINYINT}
      
      
        and status = #{status,jdbcType=TINYINT}
      
      
        and endTime = #{endTime,jdbcType=TIMESTAMP}
      
      
        and businessLicense = #{businessLicense,jdbcType=VARCHAR}
      
      
        and registerNumber = #{registerNumber,jdbcType=VARCHAR}
      
      
        and identityType = #{identityType,jdbcType=TINYINT}
      
      
        and identityCardOn = #{identityCardOn,jdbcType=VARCHAR}
      
      
        and identityCardOff = #{identityCardOff,jdbcType=VARCHAR}
      
      
        and terminals = #{terminals,jdbcType=VARCHAR}
      
      
        and serviceType = #{serviceType,jdbcType=VARCHAR}
      
      
        and serviceAlipay = #{serviceAlipay,jdbcType=VARCHAR}
      
      
        and serviceWeixin = #{serviceWeixin,jdbcType=VARCHAR}
      
      
			and (agentSerialNo = #{searchValue,jdbcType=VARCHAR}
				or
				companyName = #{searchValue,jdbcType=VARCHAR}
				or
				contactMan = #{searchValue,jdbcType=VARCHAR}
				or
				phone = #{searchValue,jdbcType=VARCHAR}
				or
				tel = #{searchValue,jdbcType=VARCHAR}
			)
