mapper.select:
		select
		
		from sys_transaction
		
			
				status = #{status,jdbcType=INTEGER}
			
			
				and DATE_FORMAT(transTime, '%Y-%m-%d') >=  #{startTransTime}
			
			
				and DATE_FORMAT(transTime, '%Y-%m-%d') <=  #{endTransTime}
			
			
				and channel = #{channel,jdbcType=INTEGER}
